mapper.select:
select s.shop_id, s.shop_name, s.shop_tel, s.shop_desc, s.shop_addr, s.priority, s.shop_img, s.create_time, s.last_edit_time, s.status, s.shop_advice, a.area_id, a.area_name, sc.shopcategory_id, sc.shopcategory_name from tb_shop s, tb_area a, tb_shopcategory sc
and s.shop_name like '%${shopCondition.shopName}%'
and s.status = #{shopCondition.status}
and s.shop_area_id = #{shopCondition.area.areaId}
and s.shop_shopcategory_id = #{shopCondition.shopCategory.shopCategoryId}
and s.shop_user_id = #{shopCondition.user.userId}
and s.shop_area_id=a.area_id and s.shop_shopcategory_id=sc.shopcategory_id
order by s.priority desc limit #{start}, #{size};

select
count(1)
from tb_shop s, tb_area a, tb_shopcategory sc


          and s.shop_name like '%${shopCondition.shopName}%'


          and s.status = #{shopCondition.status}


          and s.shop_area_id = #{shopCondition.area.areaId}


          and s.shop_shopcategory_id = #{shopCondition.shopCategory.shopCategoryId}


          and s.shop_user_id = #{shopCondition.user.userId}

      and s.shop_area_id=a.area_id
      and s.shop_shopcategory_id=sc.shopcategory_id

mapper.update:
update tb_shop
shop_name=#{shopName}, priority=#{priority}, shop_img=#{shopImg}, shop_addr=#{shopAddr}, shop_desc=#{shopDesc}, shop_tel=#{shopTel}, shop_advice=#{shopAdvice}, status=#{status}, last_edit_time=#{lastEditTime}, shop_area_id=#{area.areaId}, shop_shopcategory_id=#{shopCategory.shopCategoryId}
where shop_id = #{shopId};
